import React, { useState } from 'react';
import './PaymentForm.css';

function PaymentForm({ onSubmit, selectedPlan, price }) {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [cardType, setCardType] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        onSubmit();
    };

    const handleCardNumberChange = (event) => {
        const inputCardNumber = event.target.value.replace(/ /g, '');
        let cardType = '';
        if (/^4/.test(inputCardNumber)) {
            cardType = 'visa';
        } else if (/^5[1-5]/.test(inputCardNumber)) {
            cardType = 'mastercard';
        } else if (/^3[47]/.test(inputCardNumber)) {
            cardType = 'amex';
        }
        setCardType(cardType);

        const formattedCardNumber = inputCardNumber.replace(/(\d{4})/g, '$1 ').trim();
        setCardNumber(formattedCardNumber);
    };

    const validateFirstName = () => {
        return firstName.length >= 3 && !/\d/.test(firstName);
    };

    const validateLastName = () => {
        return lastName.length === 0 || (lastName.length >= 1 && !/\d/.test(lastName));
    };

    const validateCardNumber = () => {
        if (cardType === 'visa') {
            return /^4\d{3}(\s\d{4}){3}$/.test(cardNumber);
        } else if (cardType === 'mastercard') {
            return /^5[1-5]\d{2}(\s\d{4}){3}$/.test(cardNumber);
        } else if (cardType === 'amex') {
            return /^3[47]\d{2}(\s\d{4}){2}$/.test(cardNumber);
        } else {
            return false;
        }
    };

    const validateExpiryDate = () => {
        const [month, year] = expiryDate.split('/');
        if (month < 1 || month > 12) {
            return false;
        }
        const currentYear = new Date().getFullYear();
        const currentMonth = new Date().getMonth() + 1;
        const maxYear = currentYear + 25;
        if (year < currentYear || year > maxYear) {
            return false;
        }
        if (year === currentYear && month < currentMonth) {
            return false;
        }
        return true;
    };

    const validateCvv = () => {
        return /^\d{3,4}$/.test(cvv);
    };

    const canSubmit = () => {
        return validateFirstName() && validateLastName() && validateCardNumber() && validateExpiryDate() && validateCvv();
    };

    return (
        <div className="payment-form-container">
            <h2>Configura tu tarjeta de crédito o débito</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="firstName">Nombre</label>
                    <input
                        type="text"
                        id="firstName"
                        value={firstName}
                        onChange={(event) => setFirstName(event.target.value)}
                        required
                    />
                    {!validateFirstName() &&  ( <p className="error-message">El nombre debe tener al menos 3 letras y no debe contener números.</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="lastName">Apellido</label>
                    <input
                        type="text"
                        id="lastName"
                        value={lastName}
                        onChange={(event) => setLastName(event.target.value)}
                        required
                    />
                    {!validateLastName() && (
                        <p className="error-message">El apellido no debe contener números.</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="cardNumber">Número de tarjeta</label>
                    <input
                        type="text"
                        id="cardNumber"
                        value={cardNumber}
                        onChange={handleCardNumberChange}
                        required
                    />
                    {!validateCardNumber() && (
                        <p className="error-message">El número de tarjeta es inválido.</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="expiryDate">Fecha de expiración</label>
                    <input
                        type="text"
                        id="expiryDate"
                        placeholder="MM/AA"
                        value={expiryDate}
                        onChange={(event) => setExpiryDate(event.target.value)}
                        required
                    />
                    {!validateExpiryDate() && (
                        <p className="error-message">La fecha de expiración es inválida.</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="cvv">CVV</label>
                    <input
                        type="text"
                        id="cvv"
                        value={cvv}
                        onChange={(event) => setCvv(event.target.value)}
                        required
                    />
                    {!validateCvv() && (
                        <p className="error-message">El CVV es inválido.</p>
                    )}
                </div>
                <div className="form-group">
                    <label htmlFor="cardType">Tipo de tarjeta</label>
                    <input
                        type="text"
                        id="cardType"
                        value={cardType}
                        readOnly
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="selectedPlan">Plan seleccionado</label>
                    <input
                        type="text"
                        id="selectedPlan"
                        value={selectedPlan}
                        readOnly
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="price">Precio</label>
                    <input
                        type="text"
                        id="price"
                        value={price}
                        readOnly
                    />
                </div>
                <button type="submit" disabled={!canSubmit()}>Pagar</button>
            </form>
        </div>
    );
}

export default PaymentForm;
