import React, { useState } from 'react';
import SignUpForm from './SignUpForm';
import './App.css';

function Hero() {
    const [showSignUpForm, setShowSignUpForm] = useState(false);

    const handleEmpezarClick = () => {
        setShowSignUpForm(true);
    };

    return (
        <div className="hero">
            <h1>Películas y series ilimitadas y mucho más.</h1>
            <p>Disfruta donde quieras. Cancela en cualquier momento.</p>
            <p>¿Quieres ver algo
                ya? Escribe tu dirección de correo para crear una suscripción a Netflix o reactivarla.</p>
            <div>
                {!showSignUpForm ? (
                    <button onClick={handleEmpezarClick}>Empezar</button>
                ) : (
                    <SignUpForm />
                )}
            </div>
        </div>
    );
}

export default Hero;
