import React from 'react';

function Confirmation({ firstName, lastName, cardNumber, expiryDate, cvv, selectedPlan, price }) {
  return (
    <div>
      <h2>Confirmació de pagament:</h2>
      <p>Nom: {firstName}</p>
      <p>Cognom: {lastName}</p>
      <p>Número de targeta: {cardNumber}</p>
      <p>Data d'expiració: {expiryDate}</p>
      <p>CVV: {cvv}</p>
      <p>Pla seleccionat: {selectedPlan}</p>
      <p>Preu: {price} €</p>
    </div>
  );
}

export default Confirmation;
