import React, { useState } from 'react';
import './PaymentForm.css';

function PaymentForm({ onSubmit, selectedPlan, price, onChangePlan }) {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');
  const [cardType, setCardType] = useState('');
  const [errors, setErrors] = useState([]);

  const handleFirstNameChange = (event) => {
    setFirstName(event.target.value);
  };

  const handleLastNameChange = (event) => {
    setLastName(event.target.value);
  };

  const handleCardNumberChange = (event) => {
    setCardNumber(event.target.value);
    const cardNumberWithoutSpaces = event.target.value.replace(/ /g, '');
    if (/^4/.test(cardNumberWithoutSpaces)) {
      setCardType('visa');
    } else if (/^5[1-5]/.test(cardNumberWithoutSpaces)) {
      setCardType('mastercard');
    } else if (/^3[47]/.test(cardNumberWithoutSpaces)) {
      setCardType('amex');
    } else {
      setCardType('');
    }
  };

  const handleExpiryDateChange = (event) => {
    setExpiryDate(event.target.value);
  };

  const handleCvvChange = (event) => {
    setCvv(event.target.value);
  };

  const handlePlanChange = (event) => {
    onChangePlan(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const currentYear = new Date().getFullYear();
    const expiryYear = parseInt(expiryDate.slice(3));
    const expiryMonth = parseInt(expiryDate.slice(0, 2));
    const cardRegex = /^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|3[47][0-9]{13})$/;
    let errorList = [];

    if (firstName.length < 3 || /\d/.test(firstName)) {
      errorList.push('El nombre debe tener mínimo 3 caracteres y no puede tener números');
    }
    if (lastName.length > 0 && (lastName.length < 1 || /\d/.test(lastName))) {
      errorList.push('Apellidos deben tener mínimo un caratero y no pueden tener números');
    }
    if (!cardRegex.test(cardNumber)) {
      errorList.push('Número de tarjeta debe tener un formato válido para una tarjeta Visa, MasterCard o American Express');
    }
    if (expiryYear < currentYear || expiryYear > currentYear + 25 || (expiryMonth < 1 || expiryMonth > 12)) {
      errorList.push('Fecha de vencimiento no válida');
    }
    if (cvv.length !== 3 && cvv.length !== 4) {
      errorList.push('El CVV debe serde 3 o 4 dígitos');
    }

    if (errorList.length > 0) {
      setErrors(errorList);
      return;
    }

    onSubmit({
      firstName,
      lastName,
      cardNumber,
      expiryDate,
      cvv,
      selectedPlan,
      price,
    });
 

}

return (
<form className="PaymentForm" onSubmit={handleSubmit}>
<div className="PaymentForm__field">
<label htmlFor="firstName">Nombre</label>
<input type="text" id="firstName" name="firstName" value={firstName} onChange={handleFirstNameChange} />
</div>
<div className="PaymentForm__field">
<label htmlFor="lastName">Apellidos</label>
<input type="text" id="lastName" name="lastName" value={lastName} onChange={handleLastNameChange} />
</div>
<div className="form-group">
                <label htmlFor="cardNumber">Número de tarjeta</label>
                <input type="text" id="cardNumber" value={cardNumber} onChange={handleCardNumberChange} />
                {cardType && <img  className='ima' src={require(`./img/${cardType}.png`)} alt={cardType} />}
            </div>
<div className="PaymentForm__field PaymentForm__field--halfWidth">
<label htmlFor="expiryDate">Fecha de vencimiento (MM/AA)</label>
<input type="text" id="expiryDate" name="expiryDate" value={expiryDate} onChange={handleExpiryDateChange} />
</div>
<div className="PaymentForm__field PaymentForm__field--halfWidth">
<label htmlFor="cvv">CVV</label>
<input type="text" id="cvv" name="cvv" value={cvv} onChange={handleCvvChange} />
</div>
<div className="PaymentForm__field">
<label htmlFor="selectedPlan">Plan seleccionado</label>
<select id="selectedPlan" name="selectedPlan" value={selectedPlan} onChange={handlePlanChange}>
<option value="basic">Básico</option>
<option value="premium">Premium</option>
<option value="ultra">Ultra</option>
</select>
<span className="PaymentForm__price">{price} €/mes</span>
</div>
{errors.length > 0 && (
<div className="PaymentForm__errorList">
<ul>
{errors.map((error) => (
<li key={error}>{error}</li>
))}
</ul>
</div>
)}
<button type="submit">Pagar</button>
</form>
);
}

export default PaymentForm;
