import React, { useState } from 'react';
import './PaymentForm.css';
import PlanSelection from './PlanSelection';

function PaymentForm({ onSubmit, selectedPlan, price, onPlanChange }) {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [cardType, setCardType] = useState('');
    const [errors, setErrors] = useState([]);
    const [showPlanSelection, setShowPlanSelection] = useState(false);

    const handleFirstNameChange = (event) => {
        setFirstName(event.target.value);
    };

    const handleLastNameChange = (event) => {
        setLastName(event.target.value);
    };

    const handleCardNumberChange = (event) => {
        setCardNumber(event.target.value);
        const cardNumberWithoutSpaces = event.target.value.replace(/ /g, '');
        if (/^4/.test(cardNumberWithoutSpaces)) {
            setCardType('visa');
        } else if (/^5[1-5]/.test(cardNumberWithoutSpaces)) {
            setCardType('mastercard');
        } else if (/^3[47]/.test(cardNumberWithoutSpaces)) {
            setCardType('amex');
        } else {
            setCardType('');
        }
    };

    const handleExpiryDateChange = (event) => {
        setExpiryDate(event.target.value);
    };

    const handleCvvChange = (event) => {
        setCvv(event.target.value);
    };

    const handlePlanChange = (event) => {
        onPlanChange(event.target.value);
        setShowPlanSelection(false);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        const currentYear = new Date().getFullYear();
        const expiryYear = parseInt(expiryDate.slice(3));
        const expiryMonth = parseInt(expiryDate.slice(0, 2));
        const cardRegex = /^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|3[47][0-9]{13})$/;
        let errorList = [];

        if (
            firstName.length < 3 ||
            /\d/.test(firstName)
        ) {
            errorList.push('El nombre debe tener mínimo 3 caracteres y no puede tener números');
        }
        if (
            lastName.length > 0 &&
            (lastName.length < 1 || /\d/.test(lastName))
        ) {
            errorList.push('Apellidos deben tener mínimo un caratero y no pueden tener números');
        }
        if (
            !cardRegex.test(cardNumber)
        ) {
            errorList.push('Número de tarjeta debe tener un formato válido para una tarjeta Visa, MasterCard o American Express');
        }
        if (
            expiryYear < currentYear ||
            expiryYear > currentYear + 25 ||
            (expiryMonth < 1 || expiryMonth > 12)
        ) {
            errorList.push('Fecha de vencimiento no válida');
        }
        if (
            cvv.length !== 3 &&
            cvv.length !== 4
        ) {
            errorList.push('El CVV debe serde 3 o 4 dígitos');
        }



        if (errorList.length === 0) {
            onSubmit({
                firstName,
                lastName,
                cardNumber,
                expiryDate,
                cvv,
                selectedPlan,
                price
            });
        } else {
            setErrors(errorList);
        }
    };

    return (
        <div className="payment-form">
            <h2>Ingresa tus datos de pago</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="first-name">Nombre</label>
                    <input type="text" id="first-name" value={firstName} onChange={handleFirstNameChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="last-name">Apellidos</label>
                    <input type="text" id="last-name" value={lastName} onChange={handleLastNameChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="card-number">Número de tarjeta</label>
                    <input type="text" id="card-number" value={cardNumber} onChange={handleCardNumberChange} />
                    {cardType && <img src={`/images/${cardType}-logo.png`} alt={cardType} />}
                </div>
                <div className="form-group">
                    <label htmlFor="expiry-date">Fecha de vencimiento</label>
                    <input type="text" id="expiry-date" placeholder="MM/YY" value={expiryDate} onChange={handleExpiryDateChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="cvv">CVV</label>
                    <input type="password" id="cvv" value={cvv} onChange={handleCvvChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="plan">Plan seleccionado:</label>
                    <div className="selected-plan">{selectedPlan} (${price})</div>
                    <button type="button" onClick={() => setShowPlanSelection(!showPlanSelection)}>Cambiar plan</button>
                    {showPlanSelection && <PlanSelection onPlanChange={handlePlanChange} />}
                </div>
                {errors.length > 0 &&
                    <div className="error-list">
                        {errors.map((error, index) => <div key={index} className="error-item">{error}</div>)}
                    </div>
                }
                <button type="submit">Pagar</button>
            </form>
        </div>
    );
}

export default PaymentForm;
