import React, { useState } from 'react';
import './PaymentForm.css';

function PaymentForm({ onSubmit, selectedPlan, price }) {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        if (
            cardNumber.length !== 16 ||
            isNaN(cardNumber) ||
            expiryDate.length !== 5 || cvv.length !== 3 || isNaN(cvv)) {
        alert('Por favor, introduce información de pago válida');
    } else {
        onSubmit();
    }
};

return (
    <div className="payment-form-container ">
        <h2>Configura tu tarjeta de crédito o débito</h2>
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="firstName">Nombre</label>
                <input
                    type="text"
                    id="firstName"
                    value={firstName}
                    onChange={(event) => setFirstName(event.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label htmlFor="lastName">Apellidos</label>
                <input
                    type="text"
                    id="lastName"
                    value={lastName}
                    onChange={(event) => setLastName(event.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label htmlFor="cardNumber">Número de tarjeta</label>
                <input
                    type="text"
                    id="cardNumber"
                    value={cardNumber}
                    onChange={(event) => setCardNumber(event.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label htmlFor="expiryDate">Fecha de vencimiento (MM/AA)</label>
                <input
                    type="text"
                    id="expiryDate"
                    value={expiryDate}
                    onChange={(event) => setExpiryDate(event.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label htmlFor="cvv">Código de seguridad (CVV)</label>
                <input
                    type="text"
                    id="cvv"
                    value={cvv}
                    onChange={(event) => setCvv(event.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <h3>
                    {selectedPlan} - {price}
                </h3>
                <button type="submit">Iniciar suscripción



                    de pago</button>

            </div>
        </form>
    </div>
);
}
export default PaymentForm;
