import React, { useState } from 'react';
import './PaymentForm.css';

function PaymentForm({ onSubmit, selectedPlan, price }) {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [cardType, setCardType] = useState('');
    const [errors, setErrors] = useState([]);
    const [completed, setCompleted] = useState(false);
    const [paymentDetails, setPaymentDetails] = useState(null);

    const handleFirstNameChange = (event) => {
        setFirstName(event.target.value);
    };

    const handleLastNameChange = (event) => {
        setLastName(event.target.value);
    };

    const handleCardNumberChange = (event) => {
        setCardNumber(event.target.value);
        const cardNumberWithoutSpaces = event.target.value.replace(/ /g, '');
        if (/^4/.test(cardNumberWithoutSpaces)) {
            setCardType('visa');
        } else if (/^5[1-5]/.test(cardNumberWithoutSpaces)) {
            setCardType('mastercard');
        } else if (/^3[47]/.test(cardNumberWithoutSpaces)) {
            setCardType('amex');
        } else {
            setCardType('');
        }
    };

    const handleExpiryDateChange = (event) => {
        setExpiryDate(event.target.value);
    };

    const handleCvvChange = (event) => {
        setCvv(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const currentYear = new Date().getFullYear();
        const expiryYear = parseInt(expiryDate.slice(3));
        const expiryMonth = parseInt(expiryDate.slice(0, 2));
        const cardRegex = /^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|3[47][0-9]{13})$/;
        let errorList = [];

        if (
            firstName.length < 3 ||
            /\d/.test(firstName)
        ) {
            errorList.push('El nombre debe tener mínimo 3 caracteres y no puede tener números');
        }
        if (
            lastName.length > 0 &&
            (lastName.length < 1 || /\d/.test(lastName))
        ) {
            errorList.push('Apellidos deben tener mínimo un caratero y no pueden tener números');
        }
        if (
            !

cardRegex.test(cardNumber) ||
cardType === ''
) {
errorList.push('El número de tarjeta es inválido o no corresponde a una tarjeta Visa, Mastercard o American Express');
}
if (
expiryDate.length !== 5 ||
expiryMonth < 1 ||
expiryMonth > 12 ||
expiryYear < currentYear ||
expiryYear > currentYear + 10
) {
errorList.push('La fecha de expiración es inválida');
}
if (
cvv.length !== 3 ||
!/^\d+$/.test(cvv)
) {
errorList.push('El código CVV es inválido');
}


    if (errorList.length > 0) {
        setErrors(errorList);
    } else {
        setPaymentDetails({
            firstName,
            lastName,
            cardNumber,
            expiryDate,
            cvv,
            selectedPlan,
            price
        });
        setCompleted(true);
        onSubmit();
    }
};

return (
    <form className="PaymentForm" onSubmit={handleSubmit}>
        {!completed ? (
            <>
                <h2>Paga tu suscripción</h2>
                <div className="PaymentForm__field">
                    <label htmlFor="firstName">Nombre:</label>
                    <input
                        id="firstName"
                        type="text"
                        value={firstName}
                        onChange={handleFirstNameChange}
                        required
                    />
                </div>
                <div className="PaymentForm__field">
                    <label htmlFor="lastName">Apellidos:</label>
                    <input
                        id="lastName"
                        type="text"
                        value={lastName}
                        onChange={handleLastNameChange}
                    />
                </div>
                <div className="PaymentForm__field">
                    <label htmlFor="cardNumber">Número de tarjeta:</label>
                    <input
                        id="cardNumber"
                        type="text"
                        value={cardNumber}
                        onChange={handleCardNumberChange}
                        required
                    />
                    {cardType && (
                        <img
                            className="PaymentForm__cardType"
                            src={`/icons/${cardType}.svg`}
                            alt={cardType}
                        />
                    )}
                </div>
                <div className="PaymentForm__field">
                    <label htmlFor="expiryDate">Fecha de expiración (MM/AA):</label>
                    <input
                        id="expiryDate"
                        type="text"
                        value={expiryDate}
                        onChange={handleExpiryDateChange}
                        required
                    />
                </div>
                <div className="PaymentForm__field">
                    <label htmlFor="cvv">Código CVV:</label>
                    <input
                        id="cvv"
                        type="text"
                        value={cvv}
                        onChange={handleCvvChange}
                        required
                    />
                </div>
                <button type="submit">Pagar</button>
                {errors.length > 0 && (
                    <ul className="PaymentForm__errors">
                        {errors.map((error, index) => (
                            <li key={index}>{error}</li>
                        ))}
                    </ul>
                )}
            </>
        ) : (
            <>
                <h2>¡Gracias por tu pago!</h2>
                <p>Tu suscripción {selectedPlan} ha sido procesada.</p>
                <p>Recibimos tu pago de ${price}.</p>
            </>
        )}
    </form>
);
}

export default PaymentForm
