import React, { useState } from 'react';

function SignUpForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const validatePassword = (password) => {
    return password.length >= 6 && password.length <= 60;
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    if (!validateEmail(e.target.value)) {
      setEmailError('Introduce una dirección de correo electrónico válida');
    } else {
      setEmailError('');
    }
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    if (!validatePassword(e.target.value)) {
      setPasswordError('La contraseña debe tener entre 6 y 60 caracteres');
    } else {
      setPasswordError('');
    }
  };

  const handleSiguienteClick = () => {
    if (!validateEmail(email)) {
      setEmailError('Introduce una dirección de correo electrónico válida');
      return;
    }

    if (!validatePassword(password)) {
      setPasswordError('La contraseña debe tener entre 6 y 60 caracteres');
      return;
    }

    // avanzar al siguiente formulario
  };

  return (
    <div>
      <h2>Crea una contraseña para empezar la suscripción</h2>
      <p>¡Faltan solo algunos pasos! También odiamos el papeleo.</p>
      <div>
        <label htmlFor="email">Email</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={handleEmailChange}
          onBlur={handleEmailChange}
        />
        {emailError && <span className="error">{emailError}</span>}
      </div>
      <div>
        <label htmlFor="password">Contraseña</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
          onBlur={handlePasswordChange}
        />
        {passwordError && <span className="error">{passwordError}</span>}
      </div>
      <button onClick={handleSiguienteClick}>Siguiente</button>
    </div>
  );
}

export default SignUpForm;
