import React, { useState } from 'react';

function SignUpForm() {
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');
  const [password, setPassword] = useState('');
  const [passwordError, setPasswordError] = useState('');

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    if (!validateEmail(e.target.value)) {
      setEmailError('Introduce una dirección de correo electrónico válida');
    } else {
      setEmailError('');
    }
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    if (e.target.value.length < 6 || e.target.value.length > 60) {
      setPasswordError('La contraseña debe tener entre 6 y 60 caracteres');
    } else {
      setPasswordError('');
    }
  };

  const handleSiguienteClick = () => {
    if (email.trim() === '') {
      setEmailError('Introduce una dirección de correo electrónico obligatoria');
    }
    if (password.trim() === '') {
      setPasswordError('Introduce una contraseña obligatoria');
    }
    if (validateEmail(email) && password.length >= 6 && password.length <= 60) {
      // avanzar al siguiente formulario
    }
  };

  return (
    <div>
      <h2>Crea una contraseña para empezar la suscripción</h2>
      <p>!Faltan solo algunos pasos!</p>
      <p>También odiamos el papeleo.</p>
      <div>
        <label htmlFor="email">Email *</label>
        <input
          id="email"
          type="email"
          placeholder="Introduce tu dirección de correo electrónico"
          value={email}
          onChange={handleEmailChange}
          onBlur={handleEmailChange}
        />
        {emailError && <span className="error">{emailError}</span>}
      </div>
      <div>
        <label htmlFor="password">Contraseña *</label>
        <input
          id="password"
          type="password"
          placeholder="Introduce tu contraseña"
          value={password}
          onChange={handlePasswordChange}
          onBlur={handlePasswordChange}
        />
        {passwordError && <span className="error">{passwordError}</span>}
      </div>
      <button onClick={handleSiguienteClick}>Siguiente</button>
    </div>
  );
}

export default SignUpForm;
