import React, { useState } from 'react';

function SignUpForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [noOfertas, setNoOfertas] = useState(false);

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const validatePassword = (password) => {
    const re = /^[A-Za-z0-9]{6,60}$/;
    return re.test(password);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
    if (e.target.value === '') {
      setEmailError('La dirección de correo es obligatoria');
    } else if (!validateEmail(e.target.value)) {
      setEmailError('Introduce una dirección de correo electrónico válida');
    } else {
      setEmailError('');
    }
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    if (e.target.value === '') {
      setPasswordError('La contraseña es obligatoria');
    } else if (!validatePassword(e.target.value)) {
      setPasswordError(
        'La contraseña debe tener entre 6 y 60 caracteres alfanuméricos'
      );
    } else {
      setPasswordError('');
    }
  };

  const handleNoOfertasChange = () => {
    setNoOfertas(!noOfertas);
  };

  const handleSiguienteClick = () => {
    if (email === '') {
      setEmailError('La dirección de correo es obligatoria');
    }
    if (password === '') {
      setPasswordError('La contraseña es obligatoria');
    }
    if (email !== '' && password !== '') {
      // avanzar al siguiente formulario
    }
  };

  return (
    <div className="signup-form">
      <h2>Crea una contraseña para empezar la suscripción</h2>
      <p>!Faltan solo algunos pasos!</p>
      <p>También odiamos el papeleo.</p>
      <div>
        <label htmlFor="email">Dirección de correo electrónico</label>
        <input
          id="email"
          type="email"
          placeholder="Introduce tu dirección de correo electrónico"
          value={email}
          onChange={handleEmailChange}
          onBlur={handleEmailChange}
        />
        {emailError && <span className="error">{emailError}</span>}
      </div>
      <div>
        <label htmlFor="password">Contraseña</label>
        <input
          id="password"
          type="password"
          placeholder="Crea una contraseña"
          value={password}
          onChange={handlePasswordChange}
          onBlur={handlePasswordChange}
        />
        {passwordError && <span className="error">{passwordError}</span>}
      </div>
      <div>
        <input
          id="no-ofertas"
          type="checkbox"
          checked={noOfertas}
          onChange={handleNoOfertasChange}
        />
        <label htmlFor="no-ofertas">
          No, no quiero ofertas especiales por correo.
        </label>
      </div>
      <button onClick={handleSiguienteClick}>Siguiente</button>
    </div>
  );
}

export default SignUpForm;
