import './PaymentForm.css';

function PaymentForm({ onSubmit, selectedPlan, price }) {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [cardType, setCardType] = useState('');
    const [errors, setErrors] = useState([]);

    const handleSubmit = (event) => {
        event.preventDefault();
        const currentYear = new Date().getFullYear();
        const expiryYear = parseInt(expiryDate.slice(3));
        const expiryMonth = parseInt(expiryDate.slice(0, 2));
        const cardRegex = /^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|3[47][0-9]{13})$/;
        let errorList = [];

        if (
            firstName.length < 3 ||
            /\d/.test(firstName)
        ) {
            errorList.push('El nombre debe tener mínimo 3 caracteres y no puede tener números');
        }
        if (
            lastName.length > 0 &&
            (lastName.length < 1 || /\d/.test(lastName))
        ) {
            errorList.push('Apellidos deben tener mínimo un caratero y no pueden tener números');
        }
        if (
            !cardRegex.test(cardNumber)
        ) {
            errorList.push('Número de tarjeta debe tener un formato válido para una tarjeta Visa, MasterCard o American Express');
        }
        if (
            expiryYear < currentYear ||
            expiryYear > currentYear + 25 ||
            (expiryMonth < 1 || expiryMonth > 12)
        ) {
            errorList.push('Fecha de vencimiento no válida');
        }
        if (
            cvv.length !== 3 &&
            cvv.length !== 4
        ) {
            errorList.push('El CVV debe ser un número de 3 o 4 cifras');
        }

        if (errorList.length > 0) {
            setErrors(errorList);
        } else {
            onSubmit();
        }
    };

    const handleCardNumberChange = (event) => {
        setCardNumber(event.target.value);
        const cardNumberWithoutSpaces = event.target.value.replace(/ /g, '');
        if (/^4/.test(cardNumberWithoutSpaces)) {
            setCardType('visa');
        } else if (/^5[1-5]/.test(cardNumberWithoutSpaces)) {
            setCardType('mastercard');
        } else if (/^3[47]/.test(cardNumberWithoutSpaces)) {
            setCardType('amex');
        } else {
            setCardType('');
        }
    };
    return (
        <form onSubmit={handleSubmit} className="payment-form">
            <h2 className="payment-form__title">Complete su pago</h2>
            {errors.length > 0 && (
                <div className="payment-form__errors">
                    {errors.map((error) => (
                        <p key={error}>{error}</p>
                    ))}
                </div>
            )}
            <div className="payment-form__group">
                <label htmlFor="firstName" className="payment-form__label">Nombre:</label>
                <input type="text" id="firstName" className="payment-form__input" value={firstName} onChange={(event) => setFirstName(event.target.value)} required />
            </div>
            <div className="payment-form__group">
                <label htmlFor="lastName" className="payment-form__label">Apellidos:</label>
                <input type="text" id="lastName" className="payment-form__input" value={lastName} onChange={(event) => setLastName(event.target.value)} required />
            </div>
            <div className="payment-form__group">
                <label htmlFor="cardNumber" className="payment-form__label">Número de tarjeta:</label>
                <input type="text" id="cardNumber" className="payment-form__input payment-form__input--card" value={cardNumber} onChange={handleCardNumberChange} required />
                {cardType === 'visa' && <img src="/images/visa-logo.png" alt="Visa" className="payment-form__card-logo" />}
                {cardType === 'mastercard' && <img src="/images/mastercard-logo.png" alt="Mastercard" className="payment-form__card-logo" />}
                {cardType === 'amex' && <img src="/images/amex-logo.png" alt="American Express" className="payment-form__card-logo" />}
            </div>
            <div className="payment-form__group payment-form__group--small">
                <label htmlFor="expiryDate" className="payment-form__label">Fecha de vencimiento:</label>
                <input type="text" id="expiryDate" className="payment-form__input payment-form__input--small" value={expiryDate} onChange={(event) => setExpiryDate(event.target.value)} placeholder="MM/AA" required />
            </div>
            <div className="payment-form__group payment-form__group--small">
                <label htmlFor="cvv" className="payment-form__label">CVV:</label>
                <input type="text" id="cvv" className="payment-form__input payment-form__input--small" value={cvv} onChange={(event) => setCvv(event.target.value)} required />
            </div>
            <div className="payment-form__group payment-form__group--price">
                <p className="payment-form__selected-plan">{selectedPlan}</p>
                <p className="payment-form__price">${price}</p>
            </div>
            <button type="submit" className="payment-form__submit-btn">Pagar</button>
        </form>
    );
}

export default PaymentForm;
