import React, { useState } from 'react';

function PaymentForm(props) {
    const [paymentDetails, setPaymentDetails] = useState({
        name: '',
        lastName: '',
        cardNumber: '',
        expirationDate: '',
        securityCode: ''
    });

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setPaymentDetails(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    return (
        <div>
            <h3>Seleccionaste el plan: {props.selectedPlan}</h3>
            <form>
                <label htmlFor="name">Nombre:</label>
                <input type="text" id="name" name="name" value={paymentDetails.name} onChange={handleInputChange} required />
                <label htmlFor="lastName">Apellidos:</label>
                <input type="text" id="lastName" name="lastName" value={paymentDetails.lastName} onChange={handleInputChange} required />
                <label htmlFor="cardNumber">Número de tarjeta:</label>
                <input type="text" id="cardNumber" name="cardNumber" value={paymentDetails.cardNumber} onChange={handleInputChange} required />
                <label htmlFor="expirationDate">Fecha de vencimiento:</label>
                <input type="text" id="expirationDate" name="expirationDate" value={paymentDetails.expirationDate} onChange={handleInputChange} required />
                <label htmlFor="securityCode">Código de seguridad:</label>
                <input type="text" id="securityCode" name="securityCode" value={paymentDetails.securityCode} onChange={handleInputChange} required />
                <button type="submit">Iniciar suscripción de pago</button>
            </form>
            <button onClick={() => props.handlePlanChange()}>Cambiar plan</button>
        </div>
    );
}

export default PaymentForm;
