import React, { useState } from 'react';
import './PaymentForm.css';

function PaymentForm({ onSubmit, selectedPlan, price }) {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');
    const [cardType, setCardType] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        const currentYear = new Date().getFullYear();
        const expiryYear = parseInt(expiryDate.slice(3));
        const expiryMonth = parseInt(expiryDate.slice(0, 2));
        const cardRegex = /^(?:4[0-9]{12}(?:[0-9]{3})?|5[1-5][0-9]{14}|3[47][0-9]{13})$/;

        if (
            firstName.length < 3 ||
            /\d/.test(firstName)
        ) {
            alert('El nombre debe tener mínimo 3 caracteres y no puede tener números');
        } else if (
            lastName.length > 0 &&
            (lastName.length < 1 || /\d/.test(lastName))
        ) {
            alert('Apellidos deben tener mínimo un caratero y no pueden tener números');
        } else if (
            !cardRegex.test(cardNumber)
        ) {
            alert('Número de tarjeta debe tener un formato válido para una tarjeta Visa, MasterCard o American Express');
        } else if (
            expiryYear < currentYear ||
            expiryYear > currentYear + 25 ||
            (expiryMonth < 1 || expiryMonth > 12)
        ) {
            alert('Fecha de vencimiento no válida');
        } else if (
            cvv.length !== 3 &&
            cvv.length !== 4
        ) {
            alert('El CVV debe ser un número de 3 o 4 cifras');
        } else {
            onSubmit();
        }
    };

    const handleCardNumberChange = (event) => {
        setCardNumber(event.target.value);
        const cardNumberWithoutSpaces = event.target.value.replace(/ /g, '');
        if (/^4/.test(cardNumberWithoutSpaces)) {
            setCardType('visa');
        } else if (/^5[1-5]/.test(cardNumberWithoutSpaces)) {
            setCardType('mastercard');
        } else if (/^3[47]/.test(cardNumberWithoutSpaces)) {
            setCardType('amex');
        } else {
            setCardType('');
        }
    };

    return (
        <div className="payment-form-container ">
            <h2>Configura tu tarjeta de crédito o débito</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="firstName">Nombre</label>
                    <input
                        type="text"
                        id="firstName"
                        value={firstName}
                        onChange={(event) => setFirstName(event.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="lastName">Apellidos</label>
                    <input
                        type="text"
                        id="lastName"
                        value={lastName}
                        onChange={(event) => setLastName(event.target.value)}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="cardNumber">Número de tarjeta</label>
                    <
                        input
                        type="text"
                        id="cardNumber"
                        value={cardNumber}
                        onChange={handleCardNumberChange}
                        required
                    />
                    {cardType}
                </div>
                <div className="form-group">
                    <label htmlFor="expiryDate">Fecha de vencimiento (MM/AA)</label>
                    <input
                        type="text"
                        id="expiryDate"
                        value={expiryDate}
                        onChange={(event) => setExpiryDate(event.target.value)}
                        placeholder="MM/AA"
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="cvv">CVV</label>
                    <input
                        type="number"
                        id="cvv"
                        value={cvv}
                        onChange={(event) => setCvv(event.target.value)}
                        required
                    />
                </div>
                <div className="selected-plan-info">
                    <p>Plan seleccionado: {selectedPlan}</p>
                    <p>Precio: ${price}€</p>
                </div>
                <button type="submit">Realizar pago</button>
            </form>
        </div>
    );
}

export default PaymentForm;
