import React, { useState } from 'react';

function SignUpForm() {
    const [email, setEmail] = useState('');
    const [emailError, setEmailError] = useState('');
    const [password, setPassword] = useState('');
    const [passwordError, setPasswordError] = useState('');

    const validateEmail = (email) => {
        const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return email.length >= 5 && email.length <= 50 && re.test(email);
    };

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
        if (e.target.value.trim() === '') {
            setEmailError('Este campo es obligatorio');
        } else if (!validateEmail(e.target.value)) {
            setEmailError('Introduce una dirección de correo electrónico válida');
        } else {
            setEmailError('');
        }
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
        if (e.target.value.length < 6 || e.target.value.length > 60) {
            setPasswordError('La contraseña debe tener entre 6 y 60 caracteres');
        } else {
            setPasswordError('');
        }
    };

    const handleSiguienteClick = () => {
        if (email.trim() === '') {
            setEmailError('Este campo es obligatorio');
        } else if (!validateEmail(email)) {
            setEmailError('Introduce una dirección de correo electrónico válida');
        } else if (password.trim() === '') {
            setPasswordError('Este campo es obligatorio');
        } else if (password.length < 6) {
            setPasswordError('Aumenta la longitud de este texto a 6 caracteres o más');
        } else {
            setPasswordError('');
            // avanzar al siguiente formulario
        }
    };

    return (
        <div>
            <h2>Crea una contraseña para empezar la suscripción</h2>
            <p>¡Faltan solo algunos pasos!</p>
            <p>También odiamos el papeleo.</p>
            <div>
                <label htmlFor="email">Email *</label>
                <input
                    id="email"
                    type="email"
                    placeholder="Introduce tu dirección de correo electrónico"
                    value={email}
                    onChange={handleEmailChange}
                    onBlur={handleEmailChange}
                />
                {emailError && <span className="error">{emailError}</span>}
            </div>
            <div>
                <label htmlFor="password">Contraseña *</label>
                <input
                    id="password"
                    type="password"
                    placeholder="Introduce tu contraseña"
                    value={password}
                    onChange={handlePasswordChange}
                    onBlur={handlePasswordChange}
                    title="La contraseña debe tener entre 6 y 60 caracteres"
                />
                {passwordError && <span className="error">{passwordError}</span>}
                {!passwordError && password.length < 6 && (
                    <span className="warning">Aumenta la longitud de este texto a 6 caracteres o más</span>
                )}
            </div>
            <div>
                <input type="checkbox" id="no-ofertas" name="no-ofertas" />
                <label htmlFor="no-ofertas">No, no quiero ofertas especiales por correo.</label>
            </div>
            <button type='submit' onClick={handleSiguienteClick}>Siguiente</button>
        </div>
    );
}

export default SignUpForm
